# -*- coding: utf-8 -*-from chatterbot import ChatBotimport sys# Uncomment the following lines to enable verbose logging# import logging# logging.basicConfig(level=logging.INFO)# Create a new instance of a ChatBotbot = ChatBot(    "Terminal",    storage_adapter="chatterbot.storage.JsonFileStorageAdapter",    logic_adapters=[        "chatterbot.logic.BestMatch"    ],    input_adapter="chatterbot.input.VariableInputTypeAdapter",    output_adapter="chatterbot.output.OutputAdapter",    database="./database.db")# print(' '.join(sys.argv[1:]))print(bot.get_response(' '.join(sys.argv[1:])))